package com.leetcode;

import java.util.ArrayList;
import java.util.List;

public class Q438 {
	/**
	 * 
	 * 
	 Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.
	
	Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 20,100.
	
	The order of output does not matter.
	
	Example 1:
	
	Input:
	s: "cbaebabacd" p: "abc"
	
	Output:
	[0, 6]
	
	Explanation:
	The substring with start index = 0 is "cba", which is an anagram of "abc".
	The substring with start index = 6 is "bac", which is an anagram of "abc".
	Example 2:
	
	Input:
	s: "abab" p: "ab"
	
	Output:
	[0, 1, 2]
	
	Explanation:
	The substring with start index = 0 is "ab", which is an anagram of "ab".
	The substring with start index = 1 is "ba", which is an anagram of "ab".
	The substring with start index = 2 is "ab", which is an anagram of "ab".
	 * 
	 * 
	 */

	public List<Integer> findAnagrams(String s, String p) {
		int[] fixed = new int[26];

		for (int i = 0; i < p.length(); i++) {
			fixed[p.charAt(i) - 'a']++;
		}
		List<Integer> result = new ArrayList<Integer>();
		int length = p.length();
		for (int i = 0; i <= s.length() - length; i++) {
			if (fixed[s.charAt(i) - 'a'] != 0) {
				String sbstr = s.substring(i, i + length);
				if (sameString(sbstr, fixed)) {
					result.add(i);
				}
			}
		}
		return result;
	}

	private boolean sameString(String sbstr, int[] fixed) {
		int[] count = new int[26];
		for (int i = 0; i < sbstr.length(); i++) {
			count[sbstr.charAt(i) - 'a']++;
		}
		for (int i = 0; i < fixed.length; i++) {
			if (count[i] != fixed[i]) {
				return false;
			}
		}
		return true;

	}

	public static void main(String[] args) {
		Q438 q = new Q438();
		// eklp
		// snif
		System.out.println(q.findAnagrams(
				"",
				"yqrbgjdwtcaxzsnifvhmou"));
	}
}
