package com.leetcode.java;

/**
 *

 Given a string that consists of only uppercase English letters, you can replace any letter in the string with another letter at most k times. Find the length of a longest substring containing all repeating letters you can get after performing the above operations.

 Note:
 Both the string's length and k will not exceed 104.

 Example 1:

 Input:
 s = "ABAB", k = 2

 Output:
 4

 Explanation:
 Replace the two 'A's with two 'B's or vice versa.
 Example 2:

 Input:
 s = "AABABBA", k = 1

 Output:
 4

 Explanation:
 Replace the one 'A' in the middle with 'B' and form "AABBBBA".
 The substring "BBBB" has the longest repeating letters, which is 4.

 *
 * @author mengchaowang
 *
 */
public class Q424Contest {

  public int characterReplacement(String s, int k) {
    char base = 'A';
    int length = 0;
    for (int i = 0; i < 26; i++) {
      char c = (char) (base + i);
      length = Math.max(length, checkLength(c, s, k));
    }
    return length;
  }

  private int checkLength(char c, String s, int k) {
    int count = 0;
    for (int i = 0; i < s.length(); i++) {
      if (s.charAt(i) != c) {
        k--;
      }
      if (k < 0) {
        return count;
      }
      count++;
    }
    return count;
  }

  public static void main(String[] args) {
    Q424Contest q = new Q424Contest();
    System.out.println(q.characterReplacement("AABABBA", 1));
    System.out.println(q.characterReplacement("ABAB", 2));
    String ls = "";
    System.out.println(q.characterReplacement(ls, 2));
    System.out.println(q.characterReplacement("AAABBBBAAA", 0));
    System.out.println(q.characterReplacement(
        "EOEMQLLQTRQDDCOERARHGAAARRBKCCMFTDAQOLOKARBIJBISTGNKBQGKKTALSQNFSABASNOPBMMGDIOETPTDICRBOMBAAHINTFLH",
        7));

    String lls = "";
    System.out.println(q.characterReplacement(lls, 909));

  }
}
